function S4(){return(((1+Math.random())*0x10000)|0).toString(16).substring(1)};function guid(){return(S4()+S4()+"-"+S4()+"-"+S4()+"-"+S4()+"-"+S4()+S4()+S4())};window.Store=function(name){this.name=name;var store=localStorage.getItem(this.name);this.records=(store&&store.split(","))||[]};_.extend(Store.prototype,{save:function(){localStorage.setItem(this.name,this.records.join(","))},create:function(model){if(!model.id)model.id=model.attributes.id=guid();localStorage.setItem(this.name+"-"+model.id,JSON.stringify(model));this.records.push(model.id.toString());this.save();return model},update:function(model){localStorage.setItem(this.name+"-"+model.id,JSON.stringify(model));if(!_.include(this.records,model.id.toString()))this.records.push(model.id.toString());this.save();return model},find:function(model){return JSON.parse(localStorage.getItem(this.name+"-"+model.id))},findAll:function(){return _.map(this.records,function(id){return JSON.parse(localStorage.getItem(this.name+"-"+id))},this)},destroy:function(model){localStorage.removeItem(this.name+"-"+model.id);this.records=_.reject(this.records,function(record_id){return record_id==model.id.toString()});this.save();return model}});Backbone.localSync=function(method,model,options){var resp;var store=model.localStorage||model.collection.localStorage;switch(method){case"read":resp=model.id?store.find(model):store.findAll();break;case"create":resp=store.create(model);break;case"update":resp=store.update(model);break;case"delete":resp=store.destroy(model);break}if(resp){options.success(resp)}else{options.error("Record not found")}};